mapper.select:
select id, password
from user
where id = #{id} and password = #{password}

select id, name, email, age
from user
where id = #{id}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}

mapper.select:
select name
from user
where id=#{id} and password=#{password}

mapper.update:
update user set 
name=#{name}, password=#{password}, 
email=#{email}, age=#{age} where id = #{id}
